import React from 'react'
import { makeStyles } from '@material-ui/core/styles'
import {
    Grid,
    InputLabel,
    MenuItem,
    Select,
    FormControl,
    Button
} from '@material-ui/core'

const useStyles = makeStyles((theme) => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120
    }
}))

export default function Accounting(props) {
    const { user } = props

    const classes = useStyles()
    const [budget, setBudget] = React.useState("")
    const [budgetName, setBudgetName] = React.useState()

    const handleChange = (event) => {
        setBudget(event.target.value)
        setBudgetName(event.target.value.name)
    }
    return (
        <div>
            {user 
            ? (<div>
                <Grid container direction="column">
                    <Grid item>
                        <FormControl className={classes.formControl}>
                            <InputLabel>Budget</InputLabel>
                            <Select
                                id="select-budget"
                                value={budgetName}
                                onChange={handleChange}
                            >
                                {user.budgets.map((budget) => (
                                    <MenuItem value={budget}>{budget.name}</MenuItem>))}
                            </Select>
                        </FormControl>
                        <Button>Create a budget</Button>
                    </Grid>
                    <Grid item>                        
                        {budget
                        ? <div>To earn: {budget.to_earn}</div>
                        : <div>Select a budget and working days to see details</div>}
                    </Grid>
                </Grid></div>)
            : (<div>Loading . . .</div>)}            
        </div>
    )
}
