import React from 'react'
import {
    Grid,
    InputLabel,
    MenuItem,
    Select,
    FormControl
} from '@material-ui/core'

export default function Accounting(props) {
    const { user } = props
    const [budget, setBudget] = React.useState()
    const handleChange = (event) => {
        setBudget(event.target.value)
    }
    return (
        <div>
            {user 
            ? (<div><Grid container column>
                <Grid item>
                    Select budget and Select working days
                    {user.budgets.map((budget) => <div>{budget.name}</div>)}
                    <FormControl>
                        <InputLabel>Budget</InputLabel>
                        <Select
                            value={budget}
                            onChange={handleChange}
                        >
                            {user.budgets.map((budget) => <div><MenuItem value={budget.name}>{budget.name}</MenuItem></div>)}                            
                        </Select>
                    </FormControl>
                </Grid>
                <Grid item>
                    Show to_earn, APY, etc
                </Grid>
            </Grid></div>)
            : (<div>Loading . . .</div>)}            
        </div>
    )
}
