import React from 'react'
import { makeStyles } from '@material-ui/core/styles'
import {
    Grid,
    InputLabel,
    MenuItem,
    Select,
    FormControl
} from '@material-ui/core'

const useStyles = makeStyles((theme) => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120
    }
}))

export default function Accounting(props) {
    const { user } = props

    const classes = useStyles()
    const [budget, setBudget] = React.useState("")

    const handleChange = (event) => {
        setBudget(event.target.value)
    }
    return (
        <div>
            {user 
            ? (<div>
                <Grid container direction="column">
                    <Grid item>
                        <FormControl className={classes.formControl}>
                            <InputLabel>Budget</InputLabel>
                            <Select
                                id="select-budget"
                                value={budget === "" ? "" : budget.name}
                                onChange={handleChange}
                            >
                                {user.budgets.map((budget) => (
                                    <MenuItem value={budget}>{budget.name}</MenuItem>))}
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item>
                        Show to_earn, APY, etc
                        {budget ? budget.name : <div>Select a budget and working days</div>}
                    </Grid>
                </Grid></div>)
            : (<div>Loading . . .</div>)}            
        </div>
    )
}
