import React from 'react'
import { makeStyles } from '@material-ui/core/styles'
import {
    Grid,
    InputLabel,
    MenuItem,
    Select,
    FormControl,
    Button
} from '@material-ui/core'
import AddBudgetDialog from './AddBudgetDialog'

import accounting from '../../funcs/accounting'

const useStyles = makeStyles((theme) => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120
    }
}))

export default function Accounting(props) {
    const { user } = props

    const classes = useStyles()
    const [budget, setBudget] = React.useState("")
    const [budgetName, setBudgetName] = React.useState()
    const [addBudgetDialog, setAddBudgetDialog] = React.useState(false)
    const [state, setState] = React.useState()


    const handleChange = (e) => {
        setState({...state, [e.target.id]: e.target.value})
    } 

    const handleSelect = (event) => {
        setBudget(event.target.value)
        setBudgetName(event.target.value.name)
    }

    const handleAddBudgetDialog = () => {
        setAddBudgetDialog(!addBudgetDialog)
    }

    const addBudget = () => {
        let { budgets, success } = accounting.addBudget(state.name)
        props.onClose()
        props.authUser()
        setBudget(budget)
        setBudgetName(budget.name)
    }

    const deleteBudget = (budget) => {
        accounting.deleteBudget(budget._id)
        props.authUser()
        setBudget('')
    }
    return (
        <div>
            {user 
            ? (<div>
                <Grid container direction="column">
                    <Grid item>
                        <FormControl className={classes.formControl}>
                            <InputLabel>Budget</InputLabel>
                            <Select
                                id="select-budget"
                                value={budgetName}
                                onChange={handleSelect}
                            >
                                {user.budgets.map((budget) => (
                                    <MenuItem value={budget}>{budget.name}</MenuItem>))}
                            </Select>
                        </FormControl>
                        <Button
                            onClick={handleAddBudgetDialog}
                        >Add a budget</Button>
                        {budget
                        ? <div>
                            <Button
                                onClick={() => deleteBudget(budget)}
                            >Delete</Button></div>
                        : null}
                        <AddBudgetDialog
                            open={addBudgetDialog}
                            handleChange={handleChange}
                            addBudget={addBudget}
                            onClose={handleAddBudgetDialog}
                            authUser={props.authUser}
                        />
                    </Grid>
                    <Grid item>                        
                        {budget
                        ? <div>To earn: {budget.to_earn}</div>
                        : <div>Select a budget and working days to see details</div>}
                    </Grid>
                </Grid></div>)
            : (<div>Loading . . .</div>)}            
        </div>
    )
}
